mapper.select:

   	select rnum, pd_id, rg_id, pd_name, price, rg_date, img, ID
			from (
		select rownum as rnum, pd_id, rg_id, pd_name, price, rg_date, img, ID
			from (
				select a.pd_id, a.rg_id, a.pd_name, a.price, a.rg_date, SUBSTR(a.img,78,200) img, b.ID from product a
				left outer join cart_tb b on a.pd_id = b.pd_id and b.id=#{curId} where a.CATEGORIES = #{cate_id}
				order by rg_date desc
				)

			where rownum    <  = #{end})
			where rnum    >  = #{start}
			order by rnum

        select rnum, pd_id, rg_id, pd_name, price, rg_date, img
			from (
		select rownum as rnum, pd_id, rg_id, pd_name, price, rg_date, img
			from (
					select pd_id, rg_id, pd_name, price, rg_date, SUBSTR(img,73,200) img from product 
				order by rg_date desc
				)
			where rownum    <  =6)
			where rnum    >  = 1
			order by rnum


   	select rnum, pd_id, rg_id, pd_name, price, rg_date, img, ID
			from (
		select rownum as rnum, pd_id, rg_id, pd_name, price, rg_date, img, ID
			from (
				select a.pd_id, a.rg_id, a.pd_name, a.price, a.rg_date, SUBSTR(a.img,78,200) img, b.ID from product a
				left outer join cart_tb b on a.pd_id = b.pd_id and b.id=#{curId} where a.pd_name LIKE '%' || #{searchTerm} || '%' 
				OR a. pd_desc LIKE '%'|| #{searchTerm} || '%'
				order by rg_date desc
				)

			where rownum    <  = #{end})
			where rnum    >  = #{start}
			order by rnum
